import shutil, sys, os
shutil.rmtree(sys.argv[1], True)
shutil.rmtree(sys.argv[2], True)
try:
  os.remove(sys.argv[3])
except OSError:
  pass
for base, _dirs, files in os.walk(sys.argv[4]):
  for f in files:
    if f.endswith('.pyc'):
      os.remove(os.path.join(base, f))

